function limbPath_3b() {
        function calcTang(side0, side1, point0, point1, point2, fac, inTan) {
            const ang1 = Math.atan2(point2[1] - point1[1], point2[0] - point1[0]);
            const ang0 = Math.atan2(point1[1] - point0[1], point1[0] - point0[0]);
            const p2relY = point2[1] - point0[1];
            const p2relX = point2[0] - point0[0];
            const pointDistance = (p2relX * (point1[1] - point0[1]) - p2relY * (point1[0] - point0[0])) / Math.max(Math.sqrt(p2relX * p2relX + p2relY * p2relY), 0.0001);
            const perpDist = easeOut(Math.abs(pointDistance), 0, length(point2, point0) / 2, 1, 0);
            const centDist = easeIn(Math.abs(side0 - side1), 0, length(point0, point2), 0, 1);
            const tangShorten = 1 - perpDist * centDist;
            const avang = (ang0 + ang1) / 2;
            const leng = inTan === true ? length(point0, point1) * fac * tangShorten : length(point1, point2) * fac * tangShorten;
            const pointSide = Number(((point2[0] - point0[0]) * (point1[1] - point0[1]) - (point2[1] - point0[1]) * (point1[0] - point0[0])).toFixed(2));
            const flipTang = inTan === true ? (pointSide >= 0 ? 1 : -1) * (Math.sign(Number((ang1 - ang0).toFixed(6))) === 1 ? 1 : -1) : (pointSide <= 0 ? -1 : 1) * (Math.sign(Number((ang0 - ang1).toFixed(6))) === 1 ? 1 : -1);
            return [Math.cos(avang) * leng * flipTang, Math.sin(avang) * leng * flipTang];
        }
        const endctrl = thisComp.layer('{{ end }}');
        const curveLinArr = mul(endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('curvature').value, 0.01);
        if (curveLinArr[1] === 0 && curveLinArr[2] === 0) {
            return createPath([
                endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p0').value,
                endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p1ikfkfreek').value,
                endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p2ikfkfreek').value,
                endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p3ikfkfreek').value,
            ], [], [], false);
        }
        else {
            const p0 = endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p0').value;
            const p1ikfkfreek = endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p1ikfkfreek').value;
            const p2ikfkfreek = endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p2ikfkfreek').value;
            const p3ikfkfreek = endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('p3ikfkfreek').value;
            const ikfkfreeksides = endctrl('ADBE Effect Parade')('Pseudo/skl_3bone_data_01')('ikfkfreeksides').value;
            const inTangs = [
                [0, 0],
                curveLinArr[1] === 0 ? [0, 0] : calcTang(ikfkfreeksides[0], ikfkfreeksides[1], p0, p1ikfkfreek, p2ikfkfreek, curveLinArr[1], true),
                curveLinArr[2] === 0 ? [0, 0] : calcTang(ikfkfreeksides[1], ikfkfreeksides[2], p1ikfkfreek, p2ikfkfreek, p3ikfkfreek, curveLinArr[2], true),
                [0, 0]
            ];
            const outTangs = [
                [0, 0],
                curveLinArr[1] === 0 ? [0, 0] : calcTang(ikfkfreeksides[1], ikfkfreeksides[0], p0, p1ikfkfreek, p2ikfkfreek, curveLinArr[1], false),
                curveLinArr[2] === 0 ? [0, 0] : calcTang(ikfkfreeksides[2], ikfkfreeksides[1], p1ikfkfreek, p2ikfkfreek, p3ikfkfreek, curveLinArr[2], false),
                [0, 0]
            ];
            return createPath([p0, p1ikfkfreek, p2ikfkfreek, p3ikfkfreek], inTangs, outTangs, false);
        }
    }
limbPath_3b();
